import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Dashboard from './components/Dashboard';
import ExchangeStatus from './components/ExchangeStatus';
import BotManager from './components/BotManager';
import PassiveQuotingConfig from './components/PassiveQuotingConfig';
import Navbar from './components/Navbar';
import { apiClient } from './services/api';

function App() {
  const [systemStatus, setSystemStatus] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch system status on mount
  useEffect(() => {
    fetchSystemStatus();

    // Refresh status every 30 seconds
    const interval = setInterval(fetchSystemStatus, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchSystemStatus = async () => {
    try {
      const response = await apiClient.get('/api/system/dashboard');
      setSystemStatus(response.data);
      setError(null);
    } catch (err) {
      console.error('Failed to fetch system status:', err);
      setError('Failed to connect to the trading bot system');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p className="text-gray-300">Loading Trading Bot System...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="text-red-500 text-xl mb-4">⚠️ Connection Error</div>
          <p className="text-gray-300 mb-4">{error}</p>
          <button 
            onClick={fetchSystemStatus}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
          >
            Retry Connection
          </button>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="min-h-screen bg-gray-900 text-white">
        <Navbar systemStatus={systemStatus} />

        <main className="container mx-auto px-4 py-6">
          <Routes>
            <Route 
              path="/" 
              element={<Dashboard systemStatus={systemStatus} onRefresh={fetchSystemStatus} />} 
            />
            <Route 
              path="/exchanges" 
              element={<ExchangeStatus onRefresh={fetchSystemStatus} />} 
            />
            <Route 
              path="/bots" 
              element={<BotManager onRefresh={fetchSystemStatus} />} 
            />
            <Route 
              path="/passive-quoting" 
              element={<PassiveQuotingConfig onRefresh={fetchSystemStatus} />} 
            />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
