import axios from 'axios';

// Create axios instance with default configuration
export const apiClient = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:8000',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
apiClient.interceptors.request.use(
  (config) => {
    // Add any auth headers here if needed
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor
apiClient.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    console.error('API Error:', error);
    return Promise.reject(error);
  }
);

// API service functions
export const api = {
  // System endpoints
  system: {
    getStatus: () => apiClient.get('/api/system/status'),
    getDashboard: () => apiClient.get('/api/system/dashboard'),
    getHealth: () => apiClient.get('/api/system/health'),
    getMetrics: () => apiClient.get('/api/system/metrics'),
  },

  // Bot endpoints
  bots: {
    getAll: () => apiClient.get('/api/bots/'),
    getRunning: () => apiClient.get('/api/bots/running'),
    getById: (id) => apiClient.get(`/api/bots/${id}`),
    create: (botConfig) => apiClient.post('/api/bots/', botConfig),
    start: (id) => apiClient.post(`/api/bots/${id}/start`),
    stop: (id) => apiClient.post(`/api/bots/${id}/stop`),
    delete: (id) => apiClient.delete(`/api/bots/${id}`),
    getStatus: () => apiClient.get('/api/bots/status/summary'),
  },

  // Exchange endpoints
  exchanges: {
    getAll: () => apiClient.get('/api/exchanges/'),
    getStatus: () => apiClient.get('/api/exchanges/status'),
    getConnected: () => apiClient.get('/api/exchanges/connected'),
    getSupported: () => apiClient.get('/api/exchanges/supported'),
    getById: (id) => apiClient.get(`/api/exchanges/${id}`),
    test: (id) => apiClient.post(`/api/exchanges/${id}/test`),
    testAll: () => apiClient.post('/api/exchanges/test-all'),
    updateCredentials: (id, credentials) => 
      apiClient.post(`/api/exchanges/${id}/credentials`, credentials),
    deleteCredentials: (id) => 
      apiClient.delete(`/api/exchanges/${id}/credentials`),
    getMarkets: (id, limit = 100) => 
      apiClient.get(`/api/exchanges/${id}/markets?limit=${limit}`),
    getBalance: (id) => apiClient.get(`/api/exchanges/${id}/balance`),
    getHealth: (id) => apiClient.get(`/api/exchanges/${id}/health`),
  },

  // Strategy endpoints
  strategies: {
    getAvailable: () => apiClient.get('/api/strategies/available-strategies'),

    // Passive quoting specific
    passiveQuoting: {
      getConfig: () => apiClient.get('/api/strategies/passive-quoting/config'),
      updateConfig: (config) => apiClient.post('/api/strategies/passive-quoting/config', config),
      getPresets: () => apiClient.get('/api/strategies/passive-quoting/presets'),
      savePreset: (name, config) => apiClient.post(`/api/strategies/passive-quoting/presets/${name}`, config),
      deletePreset: (name) => apiClient.delete(`/api/strategies/passive-quoting/presets/${name}`),
      createBot: (request) => apiClient.post('/api/strategies/passive-quoting/create', request),
      quickUpdate: (request) => apiClient.post('/api/strategies/passive-quoting/quick-update', request),
      getBotStatus: (instanceId) => apiClient.get(`/api/strategies/passive-quoting/${instanceId}/status`),
    },
    validateSymbol: (symbol, exchanges = null) => 
      apiClient.post('/api/strategies/validate-symbol', { symbol, exchanges }),
    getAvailableStrategies: () => apiClient.get('/api/strategies/available-strategies'),
  },

  // Convenience methods for direct API calls
  get: (url) => apiClient.get(url),
  post: (url, data) => apiClient.post(url, data),
  put: (url, data) => apiClient.put(url, data),
  delete: (url) => apiClient.delete(url),
};

export default api;
