import React, { useState, useEffect } from 'react';
import { 
  Shield, 
  TrendingUp, 
  TrendingDown, 
  AlertTriangle, 
  Activity, 
  DollarSign, 
  BarChart3,
  RefreshCw,
  Settings,
  Download,
  Clock
} from 'lucide-react';

const RiskMonitoring = () => {
  const [accounts, setAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState('all');
  const [positionData, setPositionData] = useState({});
  const [riskMetrics, setRiskMetrics] = useState({});
  const [loading, setLoading] = useState(false);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [timeRanges, setTimeRanges] = useState({});
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Initialize time ranges for each account
  const initializeTimeRanges = (accountList) => {
    const ranges = {};
    accountList.forEach(account => {
      ranges[account.name] = {
        startTime: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString().slice(0, 16),
        endTime: new Date().toISOString().slice(0, 16)
      };
    });
    setTimeRanges(ranges);
  };

  // Fetch accounts
  const fetchAccounts = async () => {
    try {
      const response = await fetch('/api/system/accounts');
      if (response.ok) {
        const data = await response.json();
        setAccounts(data.accounts || []);
        initializeTimeRanges(data.accounts || []);
      }
    } catch (error) {
      console.error('Error fetching accounts:', error);
    }
  };

  // Fetch position data
  const fetchPositionData = async () => {
    setLoading(true);
    try {
      const endpoints = [
        '/api/positions/summary',
        '/api/positions/net',
        '/api/positions/export/json'
      ];

      const responses = await Promise.all(
        endpoints.map(endpoint => fetch(endpoint))
      );

      const [summaryData, netData, exportData] = await Promise.all(
        responses.map(r => r.json())
      );

      setPositionData({
        summary: summaryData.summary,
        netPositions: netData.net_positions,
        detailed: exportData.detailed_positions
      });

      calculateRiskMetrics(summaryData.summary, netData.net_positions);
      setLastUpdate(new Date());
    } catch (error) {
      console.error('Error fetching position data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Calculate risk metrics
  const calculateRiskMetrics = (summary, netPositions) => {
    const metrics = {
      totalExposure: summary.long_value + summary.short_value,
      netExposure: summary.net_exposure,
      longShortRatio: summary.short_value > 0 ? summary.long_value / summary.short_value : 0,
      portfolioDiversification: Object.keys(netPositions).length,
      riskScore: calculateRiskScore(summary, netPositions),
      exchangeDistribution: summary.positions_by_exchange,
      symbolConcentration: calculateSymbolConcentration(netPositions)
    };
    setRiskMetrics(metrics);
  };

  const calculateRiskScore = (summary, netPositions) => {
    let score = 0;

    // Exposure risk
    if (Math.abs(summary.net_exposure) > 10000) score += 30;
    else if (Math.abs(summary.net_exposure) > 5000) score += 20;
    else score += 10;

    // Concentration risk
    const maxPosition = Math.max(...Object.values(netPositions).map(p => Math.abs(p.value || 0)));
    const concentrationRatio = summary.long_value + summary.short_value > 0 ? 
      maxPosition / (summary.long_value + summary.short_value) : 0;

    if (concentrationRatio > 0.5) score += 40;
    else if (concentrationRatio > 0.3) score += 25;
    else score += 10;

    // Exchange diversification
    const exchangeCount = Object.keys(summary.positions_by_exchange).length;
    if (exchangeCount < 3) score += 30;
    else if (exchangeCount < 5) score += 20;
    else score += 10;

    return Math.min(score, 100);
  };

  const calculateSymbolConcentration = (netPositions) => {
    const totalValue = Object.values(netPositions).reduce((sum, pos) => sum + Math.abs(pos.value || 0), 0);
    const concentrations = {};

    Object.entries(netPositions).forEach(([symbol, pos]) => {
      concentrations[symbol] = totalValue > 0 ? (Math.abs(pos.value || 0) / totalValue) * 100 : 0;
    });

    return concentrations;
  };

  // Update time range for account
  const updateTimeRange = (accountName, field, value) => {
    setTimeRanges(prev => ({
      ...prev,
      [accountName]: {
        ...prev[accountName],
        [field]: value
      }
    }));
  };

  // Trigger trade sync for account
  const syncAccountTrades = async (accountName) => {
    const range = timeRanges[accountName];
    if (!range) return;

    try {
      const response = await fetch('/api/system/sync-trades', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          account: accountName,
          start_time: range.startTime,
          end_time: range.endTime
        })
      });

      if (response.ok) {
        await fetchPositionData();
      }
    } catch (error) {
      console.error('Error syncing trades:', error);
    }
  };

  // Auto-refresh effect
  useEffect(() => {
    let interval;
    if (autoRefresh) {
      interval = setInterval(fetchPositionData, 15000); // 15 seconds
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [autoRefresh]);

  // Initial load
  useEffect(() => {
    fetchAccounts();
    fetchPositionData();
  }, []);

  const getRiskColor = (score) => {
    if (score <= 30) return 'text-green-500';
    if (score <= 60) return 'text-yellow-500';
    return 'text-red-500';
  };

  const getRiskBgColor = (score) => {
    if (score <= 30) return 'bg-green-500/10 border-green-500/20';
    if (score <= 60) return 'bg-yellow-500/10 border-yellow-500/20';
    return 'bg-red-500/10 border-red-500/20';
  };

  const getRiskLabel = (score) => {
    if (score <= 30) return 'Low Risk';
    if (score <= 60) return 'Medium Risk';
    return 'High Risk';
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <Shield className="h-8 w-8 text-blue-500" />
          <div>
            <h1 className="text-2xl font-bold text-white">Risk Monitoring</h1>
            <p className="text-gray-400">Real-time portfolio risk analysis</p>
          </div>
        </div>
        <div className="flex items-center gap-2">
          <button
            onClick={() => setAutoRefresh(!autoRefresh)}
            className={`p-2 rounded-lg transition-colors ${
              autoRefresh ? 'bg-blue-600 text-white' : 'bg-gray-700 text-gray-300'
            }`}
            title="Auto-refresh every 15 seconds"
          >
            <Settings className="h-5 w-5" />
          </button>
          <button
            onClick={fetchPositionData}
            disabled={loading}
            className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors disabled:opacity-50"
          >
            <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </button>
        </div>
      </div>

      {/* Loading bar */}
      {loading && (
        <div className="w-full bg-gray-700 rounded-full h-1">
          <div className="bg-blue-600 h-1 rounded-full animate-pulse" style={{ width: '70%' }}></div>
        </div>
      )}

      {/* Last update info */}
      {lastUpdate && (
        <div className="bg-blue-600/10 border border-blue-600/20 rounded-lg p-3 flex items-center gap-2">
          <Clock className="h-4 w-4 text-blue-400" />
          <span className="text-blue-400 text-sm">
            Last updated: {lastUpdate.toLocaleString()}
            {autoRefresh && ' (Auto-refresh enabled)'}
          </span>
        </div>
      )}

      {/* Account Configuration */}
      <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
        <h2 className="text-lg font-semibold text-white mb-4">Account Configuration</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">Account</label>
            <select
              value={selectedAccount}
              onChange={(e) => setSelectedAccount(e.target.value)}
              className="w-full bg-gray-700 border border-gray-600 rounded-lg px-3 py-2 text-white focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="all">All Accounts</option>
              {accounts.map(account => (
                <option key={account.name} value={account.name}>
                  {account.name}
                </option>
              ))}
            </select>
          </div>

          {selectedAccount !== 'all' && timeRanges[selectedAccount] && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">Start Time (UTC)</label>
                <input
                  type="datetime-local"
                  value={timeRanges[selectedAccount].startTime}
                  onChange={(e) => updateTimeRange(selectedAccount, 'startTime', e.target.value)}
                  className="w-full bg-gray-700 border border-gray-600 rounded-lg px-3 py-2 text-white focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">End Time (UTC)</label>
                <input
                  type="datetime-local"
                  value={timeRanges[selectedAccount].endTime}
                  onChange={(e) => updateTimeRange(selectedAccount, 'endTime', e.target.value)}
                  className="w-full bg-gray-700 border border-gray-600 rounded-lg px-3 py-2 text-white focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
              <div className="flex items-end">
                <button
                  onClick={() => syncAccountTrades(selectedAccount)}
                  className="w-full bg-green-600 hover:bg-green-700 text-white rounded-lg px-4 py-2 font-medium transition-colors"
                >
                  Sync Trades
                </button>
              </div>
            </>
          )}
        </div>
      </div>

      {/* Risk Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className={`bg-gray-800 rounded-xl p-6 border ${getRiskBgColor(riskMetrics.riskScore || 0)}`}>
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Risk Score</p>
              <p className={`text-3xl font-bold ${getRiskColor(riskMetrics.riskScore || 0)}`}>
                {riskMetrics.riskScore || 0}
              </p>
              <span className={`inline-block px-2 py-1 rounded text-xs font-medium ${getRiskBgColor(riskMetrics.riskScore || 0)}`}>
                {getRiskLabel(riskMetrics.riskScore || 0)}
              </span>
            </div>
            <AlertTriangle className={`h-8 w-8 ${getRiskColor(riskMetrics.riskScore || 0)}`} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Total Exposure</p>
              <p className="text-3xl font-bold text-white">
                ${(riskMetrics.totalExposure || 0).toLocaleString()}
              </p>
              <p className="text-gray-400 text-sm">
                Net: ${(riskMetrics.netExposure || 0).toLocaleString()}
              </p>
            </div>
            <DollarSign className="h-8 w-8 text-blue-500" />
          </div>
        </div>

        <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Long/Short Ratio</p>
              <p className="text-3xl font-bold text-white">
                {(riskMetrics.longShortRatio || 0).toFixed(2)}
              </p>
              <p className="text-gray-400 text-sm">
                {positionData.summary?.open_positions || 0} positions
              </p>
            </div>
            <BarChart3 className="h-8 w-8 text-purple-500" />
          </div>
        </div>

        <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Diversification</p>
              <p className="text-3xl font-bold text-white">
                {riskMetrics.portfolioDiversification || 0}
              </p>
              <p className="text-gray-400 text-sm">symbols</p>
            </div>
            <TrendingUp className="h-8 w-8 text-green-500" />
          </div>
        </div>
      </div>

      {/* Position Details Table */}
      <div className="bg-gray-800 rounded-xl border border-gray-700 overflow-hidden">
        <div className="p-6 border-b border-gray-700">
          <h2 className="text-lg font-semibold text-white">Position Details</h2>
        </div>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-700/50">
              <tr>
                <th className="px-6 py-3 text-left text-sm font-medium text-gray-300">Symbol</th>
                <th className="px-6 py-3 text-left text-sm font-medium text-gray-300">Exchange</th>
                <th className="px-6 py-3 text-right text-sm font-medium text-gray-300">Size</th>
                <th className="px-6 py-3 text-right text-sm font-medium text-gray-300">Value</th>
                <th className="px-6 py-3 text-right text-sm font-medium text-gray-300">Side</th>
                <th className="px-6 py-3 text-right text-sm font-medium text-gray-300">Avg Price</th>
                <th className="px-6 py-3 text-right text-sm font-medium text-gray-300">Concentration</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-700">
              {Object.entries(positionData.netPositions || {}).map(([symbol, position]) => (
                position.is_open && (
                  <tr key={symbol} className="hover:bg-gray-700/50">
                    <td className="px-6 py-4 text-white font-medium">{symbol}</td>
                    <td className="px-6 py-4 text-gray-300">Net Position</td>
                    <td className="px-6 py-4 text-right text-white">{(position.size || 0).toFixed(4)}</td>
                    <td className="px-6 py-4 text-right text-white">${(position.value || 0).toLocaleString()}</td>
                    <td className="px-6 py-4 text-right">
                      <span className={`inline-block px-2 py-1 rounded text-xs font-medium ${
                        position.side === 'long' ? 'bg-green-500/20 text-green-400' :
                        position.side === 'short' ? 'bg-red-500/20 text-red-400' :
                        'bg-gray-500/20 text-gray-400'
                      }`}>
                        {position.side || 'flat'}
                      </span>
                    </td>
                    <td className="px-6 py-4 text-right text-white">
                      ${(position.avg_price || 0).toFixed(4)}
                    </td>
                    <td className="px-6 py-4 text-right text-white">
                      {(riskMetrics.symbolConcentration?.[symbol] || 0).toFixed(1)}%
                    </td>
                  </tr>
                )
              ))}
              {Object.keys(positionData.netPositions || {}).length === 0 && (
                <tr>
                  <td colSpan="7" className="px-6 py-8 text-center text-gray-400">
                    No positions found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default RiskMonitoring;
